        </Head>
  
        <Navbar />
        <div className = "home-page">
          <div className="container">
            <div className="row">
              <div className="col-lg-3">
                <Sidemenu />
              </div>
            
              <div className="col-lg-9">  
                <Carousel />
                <div className="row">
                  <MovieList Moviedata = {movies}/>
                </div>
              </div>
            </div>
          </div>
        </div>
        <Footer />
        <style jsx>{`
          .home-page {
            padding-top: 60px;
          }
        `}
    
        </style>
      </div>
    );
  }
  
}

export default Home
